seq1 = ""
seq2 = ""

transitions = [('A', 'G'), ('C', 'T'), ('G', 'A'), ('T', 'C')]
transversions = [('A', 'C'), ('A', 'T'), ('G', 'C'), ('G', 'T'), ('C', 'A'), ('C', 'G'), ('T', 'A'), ('T', 'G')]

count_transitions = 0
count_transversions = 0

for i in range(len(seq1)):
    if (seq1[i], seq2[i]) in transitions:
        count_transitions += 1
    if (seq1[i], seq2[i]) in transversions:
        count_transversions += 1

print(count_transitions)
print(count_transversions)

print(count_transitions/count_transversions)